(367, 'Tiện ích', 'Tiện ích', '<p>Dự &aacute;n Tecco Elite City l&agrave; dự &aacute;n phức hợp tiện &iacute;ch v&agrave; chung cư cao cấp đầu ti&ecirc;n tại th&agrave;nh phố Th&aacute;i Nguy&ecirc;n.</p>\r\n\r\n<p>Nơi đ&acirc;y hứa hẹn mang đến một hệ thống tiện &iacute;ch nội khu kh&ocirc;ng những đầy đủ về số lượng m&agrave; c&ograve;n hiện đại về chất lượng.</p>\r\n\r\n<p>Cư d&acirc;n Tecco Elite City sẽ c&oacute; một kh&ocirc;ng gian sống xanh, trong l&agrave;nh, v&ocirc; c&ugrave;ng gần gũi với m&ocirc;i trường qua hệ thống c&ocirc;ng vi&ecirc;n c&acirc;y xanh, vườn hoa, tiểu cảnh,&hellip;</p>\r\n\r\n<p>Ngo&agrave;i ra Tecco Elite City Th&aacute;i nguy&ecirc;n c&ograve;n sở hữu hệ thống mua sắm đa dạng gồm nhiều shophouse thương mại, cửa h&agrave;ng tiện lợi, si&ecirc;u thị, ph&ograve;ng tập Gym, Yoga, hệ thống an ninh 24/7</p>', NULL, 'vi', 4, 123, 120, '2023-05-26 02:06:59', '2023-05-26 02:09:40'),
(368, 'Utilities', NULL, NULL, NULL, 'en', 4, 123, 120, '2023-05-26 02:06:59', '2023-05-26 02:06:59'),
(369, '公用事業', NULL, NULL, NULL, 'cn', 4, 123, 120, '2023-05-26 02:06:59', '2023-05-26 02:06:59'),
(370, 'Mặt bằng', 'Mặt bằng', '<p>Dự &aacute;n Tecco Elite City Th&aacute;i nguy&ecirc;n c&oacute; quy m&ocirc; 21.000m2 gồm 6 block, mỗi block cao 32 tầng mang đến 2088 sản phẩm bao gồm: Trung t&acirc;m thương mại ở 3 khối đế, Shophouse v&agrave; căn hộ cao cấp</p>', NULL, 'vi', 5, 124, 120, '2023-05-26 02:06:59', '2023-05-26 02:09:40'),
(371, 'Ground', NULL, NULL, NULL, 'en', 5, 124, 120, '2023-05-26 02:06:59', '2023-05-26 02:06:59'),
(372, '地面', NULL, NULL, NULL, 'cn', 5, 124, 120, '2023-05-26 02:06:59', '2023-05-26 02:06:59'),
(373, 'Thiết kế căn hộ', 'Thiết kế căn hộ', '<p>Căn hộ Tecco Elite City Th&aacute;i Nguy&ecirc;n c&oacute; diện t&iacute;ch đa dạng từ 62,34 &ndash; 81,76m2 đ&aacute;p ứng c&aacute;c nhu cầu về kh&ocirc;ng gian sống cho c&aacute;c gia đ&igrave;nh từ 2-3 thế hệ. Ngo&agrave;i ra dự &aacute;n c&ograve;n c&oacute; căn hộ 2 ch&igrave;a kho&aacute; Dual Key &ndash; lựa chọn đầu tư th&ocirc;ng minh kết hợp kh&ocirc;ng gian sống linh hoạt</p>\r\n\r\n<p>Hầu hết c&aacute;c căn hộ được thiết kế tinh tế theo phong c&aacute;ch &Aacute; Đ&ocirc;ng, kiến tr&uacute;c mở gi&uacute;p c&aacute;c căn hộ sở hữu đầy đủ &aacute;nh s&aacute;ng v&agrave; gi&oacute; tự nhi&ecirc;n. Th&ecirc;m v&agrave;o đ&oacute; với mật độ x&acirc;y dựng chỉ chiếm 38%, dự &aacute;n v&ocirc; c&ugrave;ng ch&uacute; trọng đến m&ocirc;i trường từ đ&oacute; kiến tạo cuộc sống xanh, gần gũi với thi&ecirc;n nhi&ecirc;n</p>', NULL, 'vi', 6, 125, 120, '2023-05-26 02:09:59', '2023-05-26 02:10:41'),
(374, NULL, NULL, NULL, NULL, 'en', 6, 125, 120, '2023-05-26 02:09:59', '2023-05-26 02:10:41'),
(375, NULL, NULL, NULL, NULL, 'cn', 6, 125, 120, '2023-05-26 02:09:59', '2023-05-26 02:10:41'),
(376, 'Ưu điểm', 'Ưu điểm dự án', '<p>&ndash; Thời gian b&agrave;n giao nhanh, tăng gi&aacute; trị sinh lời v&agrave; dại hạn:</p>\r\n\r\n<p>&ndash; Căn hộ t&ograve;a B đẹp nhất Tecco Elite city b&agrave;n giao qu&yacute; IV/202</p>\r\n\r\n<p>&ndash; Ph&aacute;p l&yacute; đầy đủ, tạo niềm tin tuyệt đối với kh&aacute;ch h&agrave;ng. Sổ hồng vĩnh viễn</p>\r\n\r\n<p>&ndash; Chủ đầu tư uy t&iacute;n v&agrave; tiềm lực kinh tế mạnh mẽ với hơn 20 năm trong lĩnh vực Bất Động Sản : TECCO GROUP.</p>\r\n\r\n<p>&ndash; Căn hộ chuẩn Singapore, 100% căn hộ th&ocirc;ng tho&aacute;ng, đ&oacute;n nắng gi&oacute; tự nhi&ecirc;n</p>\r\n\r\n<p>&ndash; Vị tr&iacute; : Trung t&acirc;m th&agrave;nh phố thuộc h&agrave;ng lớn nhất khu T&acirc;y Bắc: Th&aacute;i Nguy&ecirc;n. Tiếp cận 4 mặt tiền</p>\r\n\r\n<p>&ndash; Chuẩn sống &rdquo; C&ocirc;ng d&acirc;n to&agrave;n cầu&rdquo; Với hệ thống 68 tiện &iacute;ch sang trọng v&agrave; đẳng cấp</p>\r\n\r\n<p>&ndash; Tổ hợp đa tiện &iacute;ch đẳng cấp quốc tế 3 tầng trung t&acirc;m thương mại 16.000m2</p>\r\n\r\n<p>&ndash; Biểu tượng mới của th&agrave;nh phố Th&aacute;i Nguy&ecirc;n: Tecco Elite City 32 tầng. Cao nhất v&ugrave;ng Việt Bắc LU&Ocirc;N LU&Ocirc;N TỎA S&Aacute;NG.</p>', NULL, 'vi', 7, 126, 120, '2023-05-26 02:09:59', '2023-05-26 02:10:41'),
(377, NULL, NULL, NULL, NULL, 'en', 7, 126, 120, '2023-05-26 02:09:59', '2023-05-26 02:10:41'),
