INSERT INTO `section_translations` (`id`, `name`, `header`, `content`, `img`, `locale`, `view`, `section_id`, `post_id`, `created_at`, `updated_at`) VALUES
(769, 'Liên kết vùng', 'Liên kết vùng', '<p>Nằm tại trung t&acirc;m kinh tế, h&agrave;nh ch&iacute;nh mới của Thủ đ&ocirc;, cư d&acirc;n The Matrix One kết nối dễ d&agrave;ng tới những c&ocirc;ng tr&igrave;nh trọng điểm của khu vực như:</p>\r\n\r\n<ul>\r\n	<li><strong>1 ph&uacute;t</strong>&nbsp;tới Bộ Ngoại giao v&agrave; đường đua F1 duy nhất tại Việt Nam</li>\r\n	<li><strong>3 ph&uacute;t&nbsp;</strong>tới bệnh viện quốc tế Hồng Ngọc đ&atilde; đi v&agrave;o vận h&agrave;nh được gần 1 năm</li>\r\n	<li><strong>3 ph&uacute;t</strong>&nbsp;tới t&ograve;a Landmark &ndash; Keangnam</li>\r\n	<li><strong>5 ph&uacute;t</strong>&nbsp;tới kh&aacute;ch sạn JW Marriott, s&acirc;n vận động Mỹ Đ&igrave;nh</li>\r\n	<li><strong>6 ph&uacute;t&nbsp;</strong>tới Bảo t&agrave;ng H&agrave; Nội</li>\r\n	<li><strong>10 ph&uacute;t</strong>&nbsp;tới Vincom Trần Duy Hưng &amp; si&ecirc;u thị Big C</li>\r\n</ul>\r\n\r\n<p><strong>Trong b&aacute;n k&iacute;nh 500m</strong>: Trường THCS Amsterdam, trường THCS Mễ Tr&igrave;, trường tiểu học L&ocirc; m&ocirc; n&ocirc; xốp, Cung văn h&oacute;a hữu nghị Việt &ndash; Trung&hellip;</p>\r\n\r\n<p><strong>15 ph&uacute;t&nbsp;</strong>tới Đại học Quốc gia H&agrave; Nội, trường ĐH Sư phạm H&agrave; Nội, Học viện B&aacute;o ch&iacute; &amp; Tuy&ecirc;n truyền</p>\r\n\r\n<p>Hiện tại, cơ sở hạ tầng ph&iacute;a T&acirc;y gần như được ho&agrave;n thiện với hệ thống giao th&ocirc;ng đồng bộ linh hoạt với c&aacute;c tuyến đường v&agrave;nh đai 3, 3.5. Nhờ hệ thống hạ tầng giao th&ocirc;ng đồng bộ, cư d&acirc;n kh&ocirc;ng chỉ dễ d&agrave;ng kết nối trong nội th&agrave;nh m&agrave; c&ograve;n dễ d&agrave;ng di chuyển, tiếp cận đến tất cả c&aacute;c v&ugrave;ng ngoại đ&ocirc;, tỉnh th&agrave;nh l&acirc;n cận để nơi đ&acirc;y trở th&agrave;nh trung t&acirc;m kết nối sầm uất bậc nhất H&agrave; Nội.</p>\r\n\r\n<p>Hơn thế nữa, theo quy hoạch ph&aacute;t triển của th&agrave;nh phố tới năm 2030,&nbsp;<strong>tuyến đường sắt Metro số 5, 6, 7</strong>&nbsp;sẽ được x&acirc;y dựng tại quận Nam Từ Li&ecirc;m sẽ gi&uacute;p cư d&acirc;n The Matrix One di chuyển nhanh ch&oacute;ng v&agrave; tiết kiệm cả về mặt thời gian v&agrave; tiền bạc trong tương lai</p>', NULL, 'vi', 3, 257, 138, '2023-06-09 21:19:44', '2023-06-09 21:22:11'),
